CREATE PROC [dbo].[InsertOrUpdateContactRoleActivity]	@Id bigint,	@ContactRoleId bigint,	@ActivityId bigintASSET NOCOUNT ONIF @Id = 0 BEGIN	INSERT INTO ContactRoleActivity (		[ContactRoleId],		[ActivityId]	)	VALUES (		@ContactRoleId,		@ActivityId	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE ContactRoleActivity SET 		[ContactRoleId] = @ContactRoleId,		[ActivityId] = @ActivityId	WHERE [Id] = @IdENDSET NOCOUNT OFF