CREATE PROC [dbo].[InsertOrUpdateContactRoleDetails]	@Id bigint,	@ContactRoleId bigint,	@AppUserId bigint,	@CreatedOn datetime,	@CreatedBy bigint,	@UpdatedOn datetime,	@UpdatedBy bigint,	@DeletedOn datetime,	@DeletedBy bigint,	@IsDeleted bitASSET NOCOUNT ONIF @Id = 0 BEGIN	INSERT INTO ContactRoleDetails (		[ContactRoleId],		[AppUserId],		[CreatedOn],		[CreatedBy],		[UpdatedOn],		[UpdatedBy],		[DeletedOn],		[DeletedBy],		[IsDeleted]	)	VALUES (		@ContactRoleId,		@AppUserId,		@CreatedOn,		@CreatedBy,		@UpdatedOn,		@UpdatedBy,		@DeletedOn,		@DeletedBy,		@IsDeleted	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE ContactRoleDetails SET 		[ContactRoleId] = @ContactRoleId,		[AppUserId] = @AppUserId,		[CreatedOn] = @CreatedOn,		[CreatedBy] = @CreatedBy,		[UpdatedOn] = @UpdatedOn,		[UpdatedBy] = @UpdatedBy,		[DeletedOn] = @DeletedOn,		[DeletedBy] = @DeletedBy,		[IsDeleted] = @IsDeleted	WHERE [Id] = @IdENDSET NOCOUNT OFF