CREATE PROC [dbo].[InsertOrUpdateContactRoleEstablishment]	@Id bigint,	@ContactRoleId bigint,	@EstablishmentId bigintASSET NOCOUNT ONIF @Id = 0 BEGIN	INSERT INTO ContactRoleEstablishment (		[ContactRoleId],		[EstablishmentId]	)	VALUES (		@ContactRoleId,		@EstablishmentId	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE ContactRoleEstablishment SET 		[ContactRoleId] = @ContactRoleId,		[EstablishmentId] = @EstablishmentId	WHERE [Id] = @IdENDSET NOCOUNT OFF