
CREATE PROC [dbo].[InsertOrUpdatePendingEstablishmentReminder]	@Id bigint,	@AppUserId bigint,	@UserDeviceId varchar(50),	@EstablishmentRemindersTaskId bigint,	@EstablishmentId bigint,	@IsOut bit,	@Message nvarchar(Max),	@SentDate datetime,	@IsSent bit,	@FormCapturedbyUser bit,	@CreatedBy bigint,	@UpdatedBy bigint,	@IsDeleted bitASSET NOCOUNT ONIF @Id = 0 BEGIN	INSERT INTO PendingEstablishmentReminder (		[AppUserId],		[UserDeviceId],		[EstablishmentRemindersTaskId],		[EstablishmentId],		[IsOut],		[Message],		[SentDate],		[IsSent],		[FormCapturedbyUser],		[CreatedOn],		[CreatedBy],		[IsDeleted]	)	VALUES (		@AppUserId,		@UserDeviceId,		@EstablishmentRemindersTaskId,		@EstablishmentId,		@IsOut,		@Message,		@SentDate,		@IsSent,		@FormCapturedbyUser,		Getdate(),		@CreatedBy,		@IsDeleted	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE PendingEstablishmentReminder SET 		[AppUserId] = @AppUserId,		[UserDeviceId] = @UserDeviceId,		[EstablishmentRemindersTaskId] = @EstablishmentRemindersTaskId,		[EstablishmentId] = @EstablishmentId,		[IsOut] = @IsOut,		[Message] = @Message,		[SentDate] = @SentDate,		[IsSent] = @IsSent,		[FormCapturedbyUser] = @FormCapturedbyUser,		[UpdatedOn] = Getdate(),		[UpdatedBy] = @UpdatedBy,		[IsDeleted] = @IsDeleted	WHERE [Id] = @Id	Select  cast(@Id as decimal) As InsertedIDENDSET NOCOUNT OFF


